import React, { useEffect } from "react"
import { useState } from "react";
import {
  useNavigate,
  Redirect,
  useHistory,
} from "react-router-dom";
import { useAuth } from "../../Context/auth"

const Profile = () => {

  const [auth,setAuth]=useAuth();
  const history = useHistory();
const checkuser=()=>{
  if(!auth.user){
    alert("you have NOT login, plz LOGIN / REGISTER");
    history.push("/Login");
  }
}
useEffect(()=>{
  checkuser();
},[]);

  return (
    <>

        {/* <h1 style={{margin:"200px"}}>Profile Page</h1> */}

<div className="card  p-3  border  border-4 border-gray rounded-5" style={{width: '30rem', margin:"100px",textAlign:"center"}}>
  <div className="card-body">
    <h1 className="card-title  text-info">Your Details</h1>
    {/* <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p> */}
    <h3>Name : {auth.user.name}</h3>
    <h4>Email : {auth.user.email}</h4>


    <a href="#" className="btn btn-danger mt-5">UPDATE</a>
  </div>
</div>


<div>
  <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal" data-bs-whatever="@mdo">Open modal for @mdo</button>
  <div className="modal fade" id="exampleModal" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div className="modal-dialog modal-dialog-centered">
      <div className="modal-content">
        <div className="modal-header">
          <h1 className="modal-title fs-5" id="exampleModalLabel">New message</h1>
          <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
        </div>
        <div className="modal-body">
          <form>
            <div className="mb-3">
              <label htmlFor="recipient-name" className="col-form-label">Recipient:</label>
              <input type="text" className="form-control" id="recipient-name" />
            </div>
            <div className="mb-3">
              <label htmlFor="message-text" className="col-form-label">Message:</label>
              <textarea className="form-control" id="message-text" defaultValue={""} />
            </div>
          </form>
        </div>
        <div className="modal-footer">
          <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
          <button type="button" className="btn btn-primary">Send message</button>
        </div>
      </div>
    </div>
  </div>
</div>



    </>
  )
}

export default Profile;
