import React from "react";
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { cdlist1 } from "../data/Data";

const Details = () => {
  //const [a, sa] = useState({});
  let params = useParams();

  //sa = cdlist1.find((e) => e.id === params);
  //   useEffect(() => {
  //     const a = cdlist1.filter((e) => e.id == 0);
  //     sa(a);
  //   }, []);
  const b = params.id;
  const p = cdlist1.filter((e) => {
    return e.id == b;
  });

  //console.log(b);
  console.log(p[0].id);
  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-md-5">
            <div className="project-info-box mt-0">
              <h5>p[0].name</h5>
              <p className="mb-0">
                Vivamus pellentesque, felis in aliquam ullamcorper, lorem tortor
                porttitor erat, hendrerit porta nunc tellus eu lectus. Ut vel
                imperdiet est. Pellentesque condimentum, dui et blandit laoreet,
                quam nisi tincidunt tortor.
              </p>
            </div>
            {/* / project-info-box */}
            <div className="project-info-box">
              <p>
                <b>Client:</b> CUPCAKE CO
              </p>
              <p>
                <b>Date:</b> 14.02.2020
              </p>
              <p>
                <b>Designer:</b> James Doe
              </p>
              <p>
                <b>Tools:</b> Illustrator
              </p>
              <p className="mb-0">
                <b>Budget:</b> $500
              </p>
            </div>
            {/* / project-info-box */}
          </div>
          {/* / column */}
          <div className="col-md-7">
            <img
              src="https://www.bootdey.com/image/400x300/FFB6C1/000000"
              alt="project-image"
              className="rounded"
            />
            <div className="project-info-box"></div>
            {/* / project-info-box */}
          </div>
          {/* / column */}
        </div>
      </div>
    </>
  );
};

export default Details;
