import React from "react";
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { cdlist1 } from "../data/Data";
import { Link } from "react-router-dom/cjs/react-router-dom.min";

const Details = () => {
  //const [a, sa] = useState({});
  let params = useParams();

  //sa = cdlist1.find((e) => e.id === params);
  //   useEffect(() => {
  //     const a = cdlist1.filter((e) => e.id == 0);
  //     sa(a);
  //   }, []);
  const b = params.id;
  const p = cdlist1.filter((e) => {
    return e.id == b;
  });

  //console.log(b);
  console.log(p[0].id);
  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-md-5">
            <div className="project-info-box mt-0">
              <h1 style={{ margin: "20px", color: "red" }}>{p[0].name}</h1>
              <p className="mb-0">{/* {d} */}</p>
            </div>

            <div className="project-info-box">
              <h4>Eligibility</h4>

              <p>
                <b>:</b> CUPCAKE CO
              </p>
              <p>
                <b>Date:</b> 14.02.2020
              </p>
              <p>
                <b>Designer:</b> James Doe
              </p>
              <p>
                <b>Tools:</b> Illustrator
              </p>
              <p className="mb-0">
                <b>Budget:</b> $500
              </p>

              <Link to="/">
                <button style={{ margin: "10px" }}>
                  Go to Official Website
                </button>
              </Link>
            </div>

            {/* / project-info-box */}
          </div>
          {/* / column */}
          <div className="col-md-7">
            <img
              style={{ margin: "10px" }}
              src={p[0].image}
              alt={p[0].name}
              className="rounded"
            />
            <div className="project-info-box"></div>
            {/* / project-info-box */}
          </div>
          {/* / column */}
        </div>
      </div>
    </>
  );
};

export default Details;
