import { useAuth } from "../../Context/auth"

const Profile = () => {

  const [auth,setAuth]=useAuth();
  const history = useHistory();
const checkuser=()=>{
  if(!auth.user){
    alert("you have NOT login, plz LOGIN / REGISTER");
    history.push("/Login");
  }
}
useEffect(()=>{
  checkuser();
},[]);

  return (
    <>

        {/* <h1 style={{margin:"200px"}}>Profile Page</h1> */}

<div className="card m-5 mt-5 p-5  border  border-5 border-gray rounded-5" style={{width: '30rem'}}>
  <div className="card-body">
    <h1 className="card-title  text-info">Your Details</h1>
    {/* <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p> */}
    <h3>Name : {auth.user.name}</h3>
    <h4>Email : {auth.user.email}</h4>


    <a href="#" className="btn btn-primary mt-5">UPDATE</a>
  </div>
</div>


    </>
  )
}

export default Profile;
